export default class Button extends Component {
    constructor(props) {
        super(props);
        this.state = {showComponent: false};
      }


       render() {
        return(<BtnCreatePersonal />, <BtnCloseCreateForm />)
       }
}

 export class BtnCreatePersonal extends Button {
  constructor(props) {
    super(props);  
    this.showFormHandler = this.showFormHandler.bind(this);
  }

  showFormHandler = (state) => {
    if (props.state === false) {
     console.log('succes')
     this.setState({
       showComponent: true
     })
    }
  }

  render() {
    return(
      <div>
       <button className="btn btn--open-create-form" onClick={this.showFormHandler}></button>
      </div>
    )
  }
 }

 export class BtnCloseCreateForm extends Button {
  constructor(props) {
    super(props, );  
    this.closeFormHandler = this.closeFormHandler.bind(this);
  }

  closeFormHandler = (props) => {
    if (props.state === true) {
      this.setState({
        showComponent: false
      })
    }
 }

  render() {
    return(
      <div>
       <button className="btn btn--close-create-form" onClick={this.closeFormHandler}></button>
      </div>
    )
  }

 }
