class BtnCreatePersonal extends React.Component {
  constructor(props) {
    super(props)
    this.state = {showComponent: false}
    this.handleClick = this.handleClick.bind(this);
  }

   handleClick () {
    this.setState({
      showComponent: true,
    });
   }

  render() {
    return (
      <div>
      <button className="btn btn--create-personal" onClick={this.handleClick}></button> {
      this.state.showComponent ? <PersonalCreate /> : null
      }
      </div>
    )
  }
}





