import React, {Component} from 'react'

// const button = () => {
//   return(
//     <div className="btn-block">
//       <button className="btn" onClick={function(){}}></button>
//     </div>
//   )
// }



export default class Buttons extends Component {
    constructor(props) {
        super(props);
        this.state = {showComponent: false};
        this.showFormHandler = this.showFormHandler.bind(this);
        this.closeFormHandler = this.closeFormHandler.bind(this);
      }

      showFormHandler = (props) => {
         if (props.state === false) {
          console.log('succes')
          this.setState({
            showComponent: true
          })
         }
      }

      closeFormHandler = (props) => {
        if (props.state === true) {
          this.setState({
            showComponent: false
          })
        }
     }

       render() {
        return(<BtnCloseCreateForm />, <BtnCreatePersonal />)
       }
}


 export class BtnCreatePersonal extends Buttons {

    render() {
      return (
        <div>
        <button className="btn btn--create-personal" onClick={this.showFormHandler}></button> {
        this.state.showComponent ? <PersonalCreate />: null
        }
        </div>
      )
    }
  }

  export class BtnCloseCreateForm extends Buttons {
    constructor(props) {
      super(props, call(this.state));
    }

    render() {
        return (
            <div>
            <button className="btn btn--close-create" onClick={this.closeFormHandler}></button> {
                this.state.showComponent ? <PersonalCreate /> : true
            }
            </div>
        )
    }

  }



  export class PersonalCreate extends Component {
    // constructor(props) {
    //   super(props);

    // }

    render() {
        return (
            <div className="block--create-personal mr-400">
            <form className="">
            <input className="input--personal-names" />
            <select className="personal-role-select">
            <option>officiant</option>
            <option>barmen</option>
            <option>hostes</option>
            </select>
            </form>
            <div className="btn btn--close-create">
                <BtnCloseCreateForm />
            </div>
            </div>
        )
    }
  }
