export default class Button extends Component {
    constructor(props) {
        super(props);
        this.state = {showComponent: false};
      }


       render() {
        return(<BtnCreatePersonal />)
       }
}

 export class BtnCreatePersonal extends Button {
  constructor(props) {
    super(props);  
    this.showFormHandler = this.showFormHandler.bind(this);
  }

  showFormHandler = () => {
    console.log('click')
    this.state = {showComponent: false} ?  
    this.setState({
       showComponent: true
     }) 
     :
      this.setState({
      showComponent: false
     })

  }

  render() {
    return(
      <div>
       <button className="btn btn--open-create-form" onClick={this.showFormHandler}></button> {
        createFormPlaceholder.render()
       }
      </div>
    )
  }
 }

 export class CreatePersonalForm extends Component {
   constructor(props) {
    super(props);
    this.state = {showComponent: false};
   }

   render () {
    return(
      <div className="">
         <form className="">
          <input className="personal-names-input" />
          <select className="personal-role-select">
            <option>officiant</option>
            <option>barmen</option>
            <option>hostes</option>
          </select>
         </form>
      </div>
    )
   }
 }
