}

 export class BtnCreatePersonal extends Button {
  constructor(props) {
    super(props);  
    this.showFormHandler = this.showFormHandler.bind(this);
  }

  showFormHandler = () => {
    this.state = {showComponent: false} ?  this.setState({
       showComponent: true
     }) : this.setState({
      show
     })

  }

  render() {
    return(
      <div>
       <button className="btn btn--open-create-form" onClick={this.showFormHandler}></button>
      </div>
    )
  }
 }
