export default class Button extends Component {
    constructor(props, boolean) {
        super(props);
        this.state = {showComponent: boolean};
        this.showFormHandler = this.showFormHandler.bind(this);
        this.closeFormHandler = this.closeFormHandler.bind(this);
      }

      showFormHandler = (props) => {
         if (props.state === false) {
          console.log('succes')
          this.setState({
            showComponent: true
          })
         }
      }

      closeFormHandler = (props) => {
        if (props.state === true) {
          this.setState({
            showComponent: false
          })
        }
     }

       render() {
        return(<BtnCloseCreateForm />, <BtnCreatePersonal />)
       }
}

