import React, { Component } from 'react'

class BtnCreatePersonal extends React.Component {
    constructor(props) {
      super(props)
      this.state = {showComponent: false}
      this.handleClick = this.handleClick.bind(this);
    }

     handleClick () {
      this.setState({
        showComponent: true,
      });
     }

    render() {
      return (
        <div>
        <button className="btn btn--create-personal" onClick={this.handleClick}></button> {
        this.state.showComponent ? <PersonalCreate /> : null
        }
        </div>
      )
    }
  }

  class PersonalCreate extends React.Component {
    constructor(props) {
        super(props)
    }

    render() {
        return (
            <div className="block--create-personal mr-400">
            <form className="">
            <input className="input--personal-names" />
            <select className="personal-role-select">
            <option>officiant</option>
            <option>barmen</option>
            <option>hostes</option>
            </select>
            </form>
            </div>
        )
    }
  }
