import React, { Component } from 'react'

export default class Buttons extends Component {
    constructor(props) {
        super(props);
        this.btnList = [];
    }
}

export class BtnCreatePersonal extends Component {
    constructor(props) {
      super(props)
      this.state = {showComponent: false}
      this.handleClick = this.handleClick.bind(this);
    }

     handleClick () {
      this.setState({
        showComponent: true,
      });
     }

    render() {
      return (
        <div>
        <button className="btn btn--create-personal" onClick={this.handleClick}></button> {
        this.state.showComponent ? <PersonalCreate /> : null
        }
        </div>
      )
    }
  }

  export class BtnCloseCreateForm extends Component {

}

  export class PersonalCreate extends Component {

    render() {
        return (
            <div className="block--create-personal mr-400">
            <form className="">
            <input className="input--personal-names" />
            <select className="personal-role-select">
            <option>officiant</option>
            <option>barmen</option>
            <option>hostes</option>
            </select>
            </form>
            <div className="btn btn--close-create"></div>
            </div>
        )
    }
  }
