import React from 'react';
import './modal.scss';

const Popup = props => {
  return(props.trigger) ? (
    <div className="popup">
       <div className="popup-inner">
        <button className="close-btn" onClick={() => props.setTrigger(false)}></button>
        { props.children };
       </div>
    </div>
  ) : ""
}

export default Popup;
