export function BtnOpenForm () {
  const [isShown, setShown] = useState(false);

  const openFormHandler = event => {
    setShown(current => !current);
    console.log('click')
  }

  return (
    <div className="block-button">
     <button className="btn btn--open-create-form" onClick={openFormHandler}></button> {
      isShown && <CreatePersonalForm />
     }
    </div>
  )
}

